mapper.select:
select bcl.id recommendId, bcl.`comment`, bcl.create_date recommendCreateDate, bcl.update_date recommendUpdateDate,
-- 有用户id，则查询用户是否点赞 count(if(a.user_id = #{userId},1,null)) isApplaud,
b.id bookId, b.name bookName, b.author, b.cover bookCover, b.douban_rate, u.id userId,u.name userName,u.wx_name, u.cover userCover, count(a.recommend_id) applaud -- (select count(id) from applaud_tb a where a.recommend_id = recommendId) applaud from book_collection_tb bc right join book_collection_list_tb bcl on bc.id = bcl.collection_id and bc.state = 1 and bc.expiration_date >= now() left join books_tb b on bcl.book_id = b.id left join users_tb u on bcl.user_id = u.id left join applaud_tb a on a.recommend_id = bcl.id where bcl.collection_id = #{collectionId}
and bcl.id != #{recommendId}
group by bcl.id order by
applaud desc, recommendUpdateDate desc recommendCreateDate desc
